package daemon.plugin.config;import java.io.File;public class TargetSystemConfiguration {    /**     * @parameter     *      expression="${javaHome}"     *      default-value="/usr/lib/jvm/java-6-sun"     * @readonly     */    private File javaHomeDirectory;    /**     * @parameter     *      expression="${applicationJarLocation}"     *      default-value="/var/lib/${project.artifactId}"     * @readonly     */    private File applicationJarLocation;    /**     * @parameter     *      expression="${daemonJarLocation}"     *      default-value="/usr/lib"     * @readonly     */    private File daemonJarLocation;}